sections.css:
// Font Fmaily
// =================================
@import "typeface";
// =================================


// Variables
// =================================
@import "variables";
// =================================


// BaselineCalc sass plugin setup and instructions
// =================================
@import "baselineCalc/index";
// =================================


// BaselineCalc sass plugin config
// =================================
@import "typeScale";
// =================================


// Utilities
// =================================
@import "utils";
// =================================


// Apply defualt font family and base font size to <html>
html{
  font-family: $type-family;
  text-rendering: optimizeLegibility !important;
  -webkit-font-smoothing: antialiased !important;
}

$a-logo--light: src="/dist/images/demo/placeholder/logo/logo-light.svg";
$a-logo--dark: src="/dist/images/demo/placeholder/logo/logo-dark.svg";

.a-logo{
    // object-fit: contain;
    // object-position: center;
    // width: auto;
    // max-width: 128px;
    width: auto;
    @include baseline-calc((
        height: 9
    ));
}




// JS Bseline auto height
// Automatically rounds the height of an image to the nearest baseline grid line.
.js-img-bs{
    width: 100%;
    object-fit: cover;
    object-position: center;
}



.u-img-circ{ border-radius: 100%;}

$layout_elegant2_appbar: base__appBar--transCompany base__appBar--hasCompany;
$layout_elegant2_appbar_company: base__appBar__toolbar--dark base__appBar__toolbar--full;
$layout_elegant2_appbar_prime: base__appBar__toolbar--light js-pinned;

label{
  position: relative;
  display: block;
  width: 100%;
  color: get-txt-colour($color-grey8, 'secondary' );
  @extend %font-byte;
  @include baseline-calc((
    padding-left: 1
  ));
}

input{
  position: relative;
  display: block;
  width: 100%;
  background: transparent;
  color: get-txt-colour($color-grey8, 'secondary' );
  border-width: 0 0 2px 0;
  border-radius: 0;
  @include baseline-calc((
    padding: 1,
    margin-bottom: 1
  ));

  &:focus,&:active{ border-color: $color-prime; outline: none;}
}

.m-pageChap{
    position: relative;
    display: block;
    width: 100%;
    @include baseline-border( "bottom", 1px, 3);
    &__head{
        position: relative;
        display: flex;
        flex-direction: row;
        align-items: baseline;
        @extend %giga;
        &__title{
            margin-right: 0.375rem;
        }
        &__cat{@extend %font-byte;}
        &__ref{
            @extend %font-byte;
            order: 3;
            margin-left: auto;
        }
    }
}

$a-u: '';
$a-ul--border: a-ul--border;

ul{ padding: 0 0 0 2em; }
li{ margin: 0; padding:0; }

// <ul> style modifiers
// ================================

// Border <ul>
// --------------------------------
// Adds a bottom border to list items
.a-ul--border{
    list-style: none;
    padding: 0;
    li{
        @include  baseline-border( "top", 1px, 0, $type: "margin" );
        // Add pading using :before and :after psudo elemets to maintain vertical rythem
        &:before,
        &:after{
            content: '';
            display: block;
            @include baseline-calc((
                padding-top: 0.5,
                padding-bottom: 0.5
            ));
        }
    }
}

.o-footer{
  position: relative;
  display: block;
  @include baseline-calc((
    padding-top: 3,
    padding-bottom: 2
  ));
}

// Common content paterns utiliing the full patern library
// =================================
@import "content-paterns/index.scss";
// =================================


// Example templates
// =================================
@import "examples/index";
// =================================

.t-post-head{
    position: relative;
    display: block;
    background-color: $color-grey8;
    z-index: 1000;
    @include baseline-calc((
        padding-top: 4,
        padding-bottom: 4,
        margin-top: -15
    ));
    @include  baseline-border( "bottom", 1px, 6, "margin" );

    // Bortder colour
    &:before{
        content: '';
        position: absolute;
        display: block;
        background-color: $color-grey8;
        top: 0;
        bottom: 0;
        @include baseline-calc((
            left: -4,
            right: -4
        ));
    }

    &__title{ @extend %mega; }
}

.t-post-meta{
    float: left;
    @include baseline-calc((
        margin-right: 4,
        margin-bottom: 2
    ));
    @include baseline-calc((
        margin-left: -8
    ), 'wide');

    // Overwride dewfualt media block sryleing
    img{
        flex: 50%!important;
        @include baseline-calc((
            height: 14,
            width: 15,
            max-width: 15,
            max-height: 14
        ));
    }
}

.t-post-thumb--lft{
    float: left;
    @include baseline-calc((
        margin-right: 5,
        margin-top: 2,
        margin-bottom: 3
    ));

    @include baseline-calc((
        margin-left: -8
    ), 'wide');
}


.t-post-thumb--rgt{
    float: right;
    @include baseline-calc((
        margin-left: 4,
        margin-top: 2,
        margin-bottom: 3
    ));
}


.t-post-aside{
    @include baseline-calc((
        padding-left: 12
    ), 'tablet');

    @include baseline-calc((
        padding-left: 10
    ), 'wide');

    &__widget{
        @include baseline-calc(( margin-bottom: 6));
    }
}

.t-doc{
    position: relative;
    display: block;
    @include baseline-calc((
        padding-top: 10,
        padding-bottom: 10
    ));
    &__page{
        position: relative;
        display: block;
        @include mq($from: 'desktop'){
            @include squish(1, 3);
        }
    }
}

// SC5 body
.sg-body{ @extend .t-doc__page; }

